<script setup lang="ts">
import { useAppStore } from '@/store'
import {
  MenuUnfoldOutlined,
  MenuFoldOutlined,
  GithubFilled,
  ProfileOutlined,
  FullscreenOutlined,
  FullscreenExitOutlined,
  DownOutlined
} from '@ant-design/icons-vue'
import { useFullscreen } from '@vueuse/core'
import type { MenuProps } from 'ant-design-vue'
import { message } from 'ant-design-vue'
import Menu from './Menu.vue'

const props = defineProps<{
  isCollapse: boolean
}>()

const avatarUrl =
  'https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png'

const appStore = useAppStore()
// collapse
const toggleCollapse = () =>
  appStore.updateSettings({ isCollapse: !props.isCollapse })

// 全屏
const { toggle, isFullscreen, isSupported } = useFullscreen()
const toggleFull = () => {
  if (!isSupported) {
    return message.error('您的浏览器不支持全屏')
  }
  toggle()
}

// 下拉框
interface DropOption {
  id: number
  value?: string
  icon?: string
  divider?: boolean
}
const dropOptions = ref<DropOption[]>([
  { id: 1, icon: '', value: '个人中心' },
  { id: 2, icon: '', value: '全局配置' },
  { id: 3, divider: true },
  { id: 4, icon: '', value: '退出登录' }
])
const handleClickItem: MenuProps['onClick'] = ({ key }) => {
  switch (key) {
    case 1:
      appStore.updateSettings({ showDrawer: true })
      break
  }
}
</script>

<template>
  <a-layout-header>
    <div class="trigger-container">
      <menu-unfold-outlined
        v-if="isCollapse"
        class="trigger"
        @click="toggleCollapse"
      />
      <menu-fold-outlined v-else class="trigger" @click="toggleCollapse" />
    </div>

    <div class="right-toolbar">
      <a-space size="middle" style="font-size: 20px">
        <tip-icon v-if="!isFullscreen" title="全屏">
          <FullscreenOutlined @click="toggleFull" />
        </tip-icon>
        <tip-icon v-else title="退出全屏">
          <FullscreenExitOutlined @click="toggleFull" />
        </tip-icon>
        <tip-icon title="文档">
          <ProfileOutlined />
        </tip-icon>
        <tip-icon title="GitHub">
          <GithubFilled />
        </tip-icon>
      </a-space>

      <a-dropdown>
        <span class="avatar-link" @click.prevent>
          <a-image
            :src="avatarUrl"
            fallback="data:image/png;base64,"
            style="border-radius: 50%"
            :width="40"
            :height="40"
            :preview="false"
          />
          <DownOutlined style="margin-left: 5px" />
        </span>
        <template #overlay>
          <a-menu @click="handleClickItem">
            <template v-for="item in dropOptions" :key="item.id">
              <a-menu-divider v-if="item.divider" />
              <a-menu-item v-else>{{ item.value }}</a-menu-item>
            </template>
          </a-menu>
        </template>
      </a-dropdown>
    </div>
  </a-layout-header>
</template>

<style lang="less" scoped>
.right-toolbar {
  display: inline-flex;
  align-items: center;
  position: absolute;
  top: 50%;
  right: 20px;
  transform: translateY(-50%);

  .avatar-link {
    cursor: pointer;
    margin-left: 20px;
  }
}
</style>
